mapper.select:
select 

from blog_user
where id = #{id,jdbcType=BIGINT}
  and username = #{username,jdbcType=VARCHAR}
  and identify_type = #{identifyType,jdbcType=INTEGER}
  and credential = #{credential,jdbcType=VARCHAR}

mapper.delete:
delete from blog_user
where id = #{id,jdbcType=BIGINT}
  and username = #{username,jdbcType=VARCHAR}
  and identify_type = #{identifyType,jdbcType=INTEGER}
  and credential = #{credential,jdbcType=VARCHAR}
